args:
/* 波紋エフェクト */
@keyframes ripple-effect {
    0% {
        box-shadow: 0 0 0 0 rgba(0, 123, 255, 0.7);
    }
    70% {
        box-shadow: 0 0 0 15px rgba(0, 123, 255, 0);
    }
    100% {
        box-shadow: 0 0 0 0 rgba(0, 123, 255, 0);
    }
}

.ripple-active {
    animation: ripple-effect 0.6s ease-out;
}
